import React, {Component} from "react";
import Display from "./component/Display";
import ButtonPanel from "./component/ButtonPanel/";
// import calculate from "../logic/calculate";
import "./App.css";

class App extends Component {

    // state = {
    //   total: null,
    //   next: null,
    //   operation: null,
    // };

    state = {
      firstNumber: "",
      secondNumber: "",
      isOperatorChosen: false,
      operator: ""
    }

  handleClick = (buttonName, buttonClass) => {
    // this.setState(calculate(this.state, buttonName));
    console.log(buttonName, buttonClass);

    if (buttonClass === "number") {
      console.log("yes");
      if (this.state.isOperatorChosen === false) {
        this.setState({firstNumber: this.state.firstNumber + buttonName});
        // console.log("hello",this.state);
      }
      else if (this.state.isOperatorChosen === true) {
        this.setState({secondNumber: this.state.secondNumber + buttonName});
        console.log("2",this.state);
      }
    }
    else if (buttonClass === "operator") {
      this.setState({isOperatorChosen: true, operator: buttonName});
    }
  }



  render() {
    console.log(this.state);
    return (
      <div className="component-app">
        <Display value={this.state.next || this.state.total || "0"} />
        <ButtonPanel clickHandler={this.handleClick} />
      </div>
    );
  };

};

export default App;
