import React, {Component} from "react";
import PropTypes from "prop-types";
import "./Button.css";

class Button extends Component {

    state = {
        firstNumber: "",
        secondNumber: "",
        isOperatorChosen: false,
        operator: ""
    }

    handleButtonClick = () => {
        this.props.clickHandler(this.props.name);
        // console.log(this.props.name);

        if (this.props.className === "number") {
            if (this.state.isOperatorChosen === false) {
                this.state.firstNumber += this.props.name;
                console.log("hello",this.state);
            }
            else if (this.state.isOperatorChosen === true) {
                this.state.secondNumber = this.props.name;
                console.log("2",this.state);
            }
        }
        else if (this.props.className === "operator") {
            console.log(this.props.name);
            this.setState({isOperatorChosen: true});
        }

    }

    render() {

        const className = [
            "component-button",
            this.props.orange ? "orange" : "",
            this.props.wide ? "wide" : ""
        ];

        return (
            <div className={className.join(" ").trim()}>
                <button onClick={this.handleButtonClick}>{this.props.name}</button>
            </div>
        )
    }
}

Button.propTypes = {
    name: PropTypes.string,
    orange: PropTypes.bool,
    wide: PropTypes.bool,
    clickHandler: PropTypes.func
}

export default Button;
